import React, { useState, useEffect } from "react";
import InNavbar from "../../Model/NavBar/InNav";
import Img from "../../../library/coming_soon.png";
import { useNavigate } from "react-router-dom";

import { UserAuth } from "../../../Server/firebase/AuthContext";
import Camera from "../../Model/svg/Camera";
import { storage, db, auth } from "../../../Server/firebase/FirebaseInitialize";
import { ref, getDownloadURL, uploadBytes } from "firebase/storage";
import { async } from "@firebase/util";
import { getDoc, doc, updateDoc } from "firebase/firestore";

const Account = () => {
  const [img, setImg] = useState("");
  const [User, setUser] = useState();

  useEffect(() => {
    getDoc(doc(db, "Users", auth.currentUser.uid)).then((docSnap) => {
      if (docSnap.exists) {
        setUser(docSnap.data());
      }
    });
    if (img) {
      const uploadImg = async () => {
        const imgRef = ref(
          storage,
          `avatar/${new Date().getTime()}- ${img.name}`
        );
        const snap = await uploadBytes(imgRef, img);
        const url = await getDownloadURL(ref(storage, snap.ref.fullPath));
        await updateDoc(doc(db, "Users", auth.currentUser.uid), {
          avatar: url,
          avatarPath: snap.ref.fullPath,
        });
        setImg("");
        console.log(snap.ref.fullPath);
        console.log(url);
      };
      uploadImg();
    }
  }, [img]);

  const { user, logout } = UserAuth();
  const navigate = useNavigate();
  const { OfflineChange } = UserAuth();

  const handleLogout = async () => {
    try {
      await OfflineChange();
      console.log("ok it  will be done");
      navigate("/");
      await logout();
      console.log("You are logged out");
    } catch (e) {
      console.log(e.message);
    }
  };

  return (
    <>
      <InNavbar />
      <section>
        <div className="profile_container">
          <div className="img_container">
            <img src={Img} alt="avatar" />
            <div className="overlay">
              <div>
                <label htmlFor="photo">
                  <Camera />
                  <input
                    type="file"
                    accept="image/*"
                    style={{ display: "none" }}
                    id="photo"
                    onChange={(e) => setImg(e.target.files[0])}
                  />
                </label>
              </div>
            </div>
          </div>
          <div className="text_container">
            <h3>user Name</h3>
            <p>Email</p>
            <hr />
            <small>Added On: ...</small>
          </div>
        </div>
      </section>
    </>
  );
};

export default Account;
