import React, { useState, useEffect } from "react";
import InNavbar from "../../Model/NavBar/InNav";
import Img from "../../../library/coming_soon.png";
import { useNavigate } from "react-router-dom";
import { UserAuth } from "../../../Server/firebase/AuthContext";
import Camera from "../../Model/svg/Camera";

const Account = () => {
  const [img, setImg] = useState("");
  // console.log(img);

  const { user, logout } = UserAuth();
  const navigate = useNavigate();
  const { OfflineChange } = UserAuth();

  const handleLogout = async () => {
    try {
      await OfflineChange();
      console.log("ok it  will be done");
      navigate("/");
      await logout();
      console.log("You are logged out");
    } catch (e) {
      console.log(e.message);
    }
  };

  return (
    <>
      <InNavbar />
      <section>
        <div className="profile_container">
          <div className="img_container">
            <img src={Img} alt="avatar" />
            <div className="overlay">
              <div>
                <label htmlFor="photo">
                  <Camera />
                  <input
                    type="file"
                    accept="image/*"
                    style={{ display: "none" }}
                    id="photo"
                    onChange={(e) => setImg(e.target.files[0])}
                  />
                </label>
              </div>
            </div>
          </div>
          <div className="text_container">
            <h3>user Name</h3>
            <p>Email</p>
            <hr />
            <small>Added On: ...</small>
          </div>
        </div>
      </section>
    </>
  );
};

export default Account;
